mapper.select:
SELECT  DISTINCT o.* FROM orderr o
INNER JOIN orderxiang ox ON o.id=ox.oid
INNER JOIN USER u ON u.id=ox.uid

	  u.id=#{uid}
	and o.oYogistics =#{order.oYogistics}

SELECT COUNT(1) FROM (
select count(*) FROM  orderr o
INNER JOIN orderxiang ox ON o.id=ox.oid
INNER JOIN USER u ON u.id=ox.uid

	u.id=#{uid}
	and o.oYogistics =#{order.oYogistics} 
)t

SELECT COUNT(1) FROM (
select count(*) FROM  orderr o
INNER JOIN orderxiang ox ON o.id=ox.oid

	o.oYogistics='待配送'
	and o.oZLogistics =#{order.oZLogistics} 
)t
